import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
;
  const handlesliceClick = () => {
    let newText = text.slice(5, 10);
    setText(newText);
  };
  const handleReplaceClick = () => {
    let newText = text.replace("Text", "Replace");
    setText(newText);
  };
  const handleConcatClick = () => {
    let newText = text + " This String is Concat";
    setText(newText);
  };
  const handleClearClick =()=>{
    let newText = ''
    setText(newText)
  }
  const handleCaptClick = () =>{
    let newText = text.replace(/\w\S*/g, (w) => (w.replace(/^\w/, (c) => c.toUpperCase())));
  }
  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  let [text, setText] = useState("");

  return (
    <React.Fragment>
    <div>
      <h2>{props.heading}</h2>
      
      <div className="  my-3">
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnChange}
          id="myBox"
          rows="8" placeholder="Enter Text here!"
        ></textarea>

        <button className="btn btn-primary m-3" onClick={handleUpClick}>
          Convert to UpperCase
        </button>

        <button className="btn btn-primary m-3" onClick={handleLowClick}>
          Convert to lowerCase
        </button>

        <button className="btn btn-primary m-3" onClick={handlesliceClick}>
          Slice
        </button>

        <button className="btn btn-primary m-3" onClick={handleReplaceClick}>
          Replacing
        </button>

        <button className="btn btn-primary m-3" onClick={handleConcatClick}>
          String concat
        </button>

        <button className="btn btn-primary m-3" onClick={handleClearClick}>
          Clear
        </button>

        {/* <button className="btn btn-primary m-3" onClick={handleConcatClick}>
          String concat
        </button>

        <button className="btn btn-primary m-3" onClick={handleConcatClick}>
          String concat
        </button>

        <button className="btn btn-primary m-3" onClick={handleConcatClick}>
          String concat
        </button>

        <button className="btn btn-primary m-3" onClick={handleConcatClick}>
          String concat
        </button> */}
      </div>
    </div>
    <div>
        <h2>Summary</h2>
        <p>Total words are: {text.split(" ").length} Total Character: {text.length}</p>
        <p>Taking Total time to read in minutes: {0.008 * text.split(' ').length}</p>
        <h3>Preview</h3>
        <p>{text}</p>
    </div>
    </React.Fragment>
  );
}

