export default function TextForm(props) {
    let [text, setText] = useState('Enter Text here!');

  return (
    <div>
        <h2>{props.heading}</h2>
      <div className="mb-3">
        
        <textarea className="form-control" vlaid="myBox" rows="8"></textarea>
        <button className="btn btn-primary my-3">Convert to UpperCase</button>
      </div>
    </div>
  );
}
