import "./App.css";
import React, { useState } from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import Alerts from "./components/Alerts";
import About from "./components/About";

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

function App() {
  const [alert, setAlert] = useState(null);
  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type,
    });
    setTimeout(() => {
      setAlert(null);
    }, 2000);
  };

  const [mode, setMode] = useState("light");
  const toggleMode = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroundColor = "#403636";
      showAlert("Dark Mode Has Been Enabled", "success");
      document.title = "TextUtils - Dark Mode";

      setInterval(() => {
        document.title = "Site is Azaming!";
      }, 2000);
      setInterval(() => {
        document.title = "Instal this Application";
      }, 1500);
    } else {
      setMode("light");
      document.body.style.backgroundColor = "white";
      showAlert("Light Mode Has Been Enabled", "success");
      document.title = "TextUtils - Light Mode";
    }
  };

  // }

  return (
    // <React.Fragment>
    //   <Router>
    //     <Navbar
    //       title="TextUtils"
    //       about="About Us"
    //       mode={mode}
    //       toggleMode={toggleMode}
    //     />
    //     <Alerts alert={alert} />

    //     <div className="container my-3">
    //       <Switch>
    //         <Route  path="/about">
    //           <About />
    //         </Route>

    //         <Route path="/">
    //           <TextForm
    //             heading="Enter the text to analyze bellow"
    //             mode={mode}
    //             toggleMode={toggleMode}
    //             showAlert={showAlert}
    //           />
    //         </Route>
    //       </Switch>
    //     </div>
    //   </Router>
    // </React.Fragment>
    <>
    <Router>


      <Navbar title="TextUtils" mode={mode} toggleMode={toggleMode}/>
      <Alert alert={alert}/>
      <div className="container my-3">

          <Routes>
            <Route exact path="/about" element={<About/>} >

            </Route>

            <Route exact path="/home" element={<TextForm showAlert={showAlert} heading="Enter your text to Analyse below" mode={mode}/>}>

            </Route>
          </Routes>


      </div>


      </Router>

    </>
  );
}

export default App;
