        <Alerts alert={alert} />

        <div className="container my-3">
          <Switch>
            <Route  path="/about">
              <About />
            </Route>

            <Route path="/">
              <TextForm
                heading="Enter the text to analyze bellow"
                mode={mode}
                toggleMode={toggleMode}
                showAlert={showAlert}
              />
            </Route>
          </Switch>
        </div>
      </Router> */}




    </React.Fragment>
  );
}

export default App;
