import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handleLenClick = () => {
    let newText = text.length();
    setText()
  }

  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  let [text, setText] = useState("Enter Text here!");

  return (
    <div>
      <h2>{props.heading}</h2>
      <div className="m-3">
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnChange}
          id="myBox"
          rows="8"
        ></textarea>

        <button className="btn btn-primary m-3" onClick={handleUpClick}>Convert to UpperCase</button>

        <button className="btn btn-primary m-3" onClick={handleLowClick}>Convert to lowerCase</button>

        <button className="btn btn-primary m-3" onClick={handleLenClick}>Find Length</button>



      </div>
    </div>
  );
}
