import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handlesliceClick = () => {
    let newText = text.slice(5, 10);
    setText(newText);
  };
  const handleReplaceClick = () => {
    let newText = text.replace("Text", "Replace");
    setText(newText);
  };
  const handleConcatClick = () => {
    let newText = text + " This String is Concat";
    setText(newText);
  };
  const handleClearClick = () => {
    let newText = "";
    setText(newText);
  };

  const handleCaptClick = () => {
    /*Explanation:

    ^\w : first character of the string
    | : or
    \s\w : first character after whitespace
    (^\w|\s\w) Capture the pattern.
    g Flag: Match all occurrences.*/

    let newText = text.replace(/(^\w|\s\w)/g, (m)=>m.toUpperCase());
    setText(newText);
  };

  const handleCopy = () => {
    let text = document.getElementById('myBox')
    // text.select();
    navigator.clipboard.writeText(text.value);
  }

  const handleExtraSpaces = () => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "))
  }


  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  let [text, setText] = useState("");

  return (
    <React.Fragment>
      <div>
        <h2>{props.heading}</h2>

        <div className="  my-3">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
            placeholder="Enter Text here!"
          ></textarea>

          <button className="btn btn-primary m-3" onClick={handleUpClick}>
            Convert to UpperCase
          </button>

          <button className="btn btn-primary m-3" onClick={handleLowClick}>
            Convert to lowerCase
          </button>

          <button className="btn btn-primary m-3" onClick={handlesliceClick}>
            Slice
          </button>

          <button className="btn btn-primary m-3" onClick={handleReplaceClick}>
            Replacing
          </button>

          <button className="btn btn-primary m-3" onClick={handleConcatClick}>
            String concat
          </button>

          <button className="btn btn-primary m-3" onClick={handleCaptClick}>
            Capitilized
          </button>

          <button className="btn btn-primary" onClick={handleCopy}> Copy Text</button>
          <button className="btn btn-primary" onClick={handleExtraSpaces}>Remove Extra Spaces</button>

          <button className="btn btn-primary m-3" onClick={handleClearClick}>
            Clear
        </button>

        </div>
      </div>
      <div>
        <h2>Summary</h2>
        <p>
          Total words are: {text.split(" ").length} Total Character:{" "}
          {text.length}
        </p>
        <p>
          Taking Total time to read in minutes: {0.008 * text.split(" ").length}
        </p>
        <h3>Preview</h3>
        <p>{text}</p>
      </div>
    </React.Fragment>
  );
}

