import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
;
  const handlesliceClick = () => {
    let newText = text.slice(5, 10);
    setText(newText);
  };
  const handleReplaceClick = () => {
    let newText = text.replace("Text", "Replace");
    setText(newText);
  };
  const handleConcatClick = () => {
    let newText = text + " This String is Concat";
    setText(newText);
  };

  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  let [text, setText] = useState("Enter Text here!");

  return (
    <div>
      <h2>{props.heading}</h2>
      
      <div className="m-3">
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnChange}
          id="myBox"
          rows="8"
        ></textarea>

        <button className="btn btn-primary m-3" onClick={handleUpClick}>
          Convert to UpperCase
        </button>

        <button className="btn btn-primary m-3" onClick={handleLowClick}>
          Convert to lowerCase
        </button>

        <button className="btn btn-primary m-3" onClick={handlesliceClick}>
          Slice
        </button>

        <button className="btn btn-primary m-3" onClick={handleReplaceClick}>
          Replacing
        </button>

        <button className="btn btn-primary m-3" onClick={handleConcatClick}>
          String concat
        </button>
      </div>
    </div>
  );
}

